class Solution { public void findSecretWord(String[] words, Master master) { List<String> list = new ArrayList<>(Arrays.asList(words)); while(!list.isEmpty()) { String word = list.get(new Random().nextInt(list.size())); int count = master.guess(word); if(count == 6) return; List<String> tempList = new ArrayList<>(); for(String s : list) { if(compare(s, word) == count) tempList.add(s); } list = tempList; } } int compare(String s, String temp) { int count = 0; for(int i = 0; i < 6; i++) { if(s.charAt(i) == temp.charAt(i)) count++; } return count; } }